mapper.update:
UPDATE COMMUNITY
SET COMMUNITY_TITLE = #{communityTitle} , COMMUNITY_CONTENT = #{communityContent}, COMMUNITY_DATE = SYSDATE
WHERE COMMUNITY_NUMBER = #{communityNumber}

UPDATE COMMUNITY
SET COMMUNITY_CNT = COMMUNITY_CNT+1
WHERE COMMUNITY_NUMBER = #{communityNumber}

mapper.select:
SELECT COUNT(*)
FROM COMMUNITY_COMMENT
WHERE COMMUNITY_NUMBER = #{communityNumber}

SELECT (SELECT COUNT(*) FROM COMMUNITY_COMMENT WHERE COMMUNITY_COMMENT.COMMUNITY_NUMBER = C.COMMUNITY_NUMBER) AS COMMENT_CNT, (SELECT COUNT(*) FROM COMMUNITY_GOOD WHERE COMMUNITY_GOOD.COMMUNITY_NUMBER = C.COMMUNITY_NUMBER) AS LIKE_CNT ,C.COMMUNITY_NUMBER, C.COMMUNITY_TITLE, C.COMMUNITY_CONTENT, C.COMMUNITY_DATE, C.COMMUNITY_CNT, C.CATEGORY_NUMBER ,C.USER_NUMBER, T.CATEGORY_NAME FROM COMMUNITY C JOIN WW_USER U ON C.USER_NUMBER = U.USER_NUMBER JOIN COMMUNITY_CATEGORY T ON C.CATEGORY_NUMBER = T.CATEGORY_NUMBER
AND C.CATEGORY_NUMBER = #{categoryNumber}
ORDER BY C.COMMUNITY_NUMBER DESC

SELECT MC.CAREER_COMPANY, MC.CAREER_TITLE, P.PFP_SYSTEM_NAME, P.PFP_UUID, MC.CAREER_START_DATE, U.USER_NICKNAME FROM WW_USER U LEFT OUTER JOIN USER_PFP P ON U.USER_NUMBER = P.USER_NUMBER LEFT OUTER JOIN MENTOR M ON U.USER_NUMBER = M.USER_NUMBER LEFT OUTER JOIN MENTOR_CAREER MC ON MC.MENTOR_NUMBER = M.MENTOR_NUMBER
WHERE U.USER_NUMBER = #{userNumber}
ORDER BY MC.CAREER_START_DATE DESC

SELECT COMMUNITY_NUMBER, COMMUNITY_TITLE, COMMUNITY_CONTENT, COMMUNITY_DATE, USER_NUMBER, COMMUNITY_CNT, COMMENT_CNT, CATEGORY_NAME, LIKE_CNT FROM( SELECT ROWNUM RN, COMMUNITY_NUMBER, COMMUNITY_TITLE, COMMUNITY_CONTENT, COMMUNITY_DATE, USER_NUMBER,COMMENT_CNT, LIKE_CNT, COMMUNITY_CNT, CATEGORY_NAME FROM( SELECT (SELECT COUNT(*) FROM COMMUNITY_COMMENT WHERE COMMUNITY_COMMENT.COMMUNITY_NUMBER = C.COMMUNITY_NUMBER) AS COMMENT_CNT, (SELECT COUNT(*) FROM COMMUNITY_GOOD WHERE COMMUNITY_GOOD.COMMUNITY_NUMBER = C.COMMUNITY_NUMBER) AS LIKE_CNT, C.COMMUNITY_NUMBER, C.COMMUNITY_TITLE, C.COMMUNITY_CONTENT, C.COMMUNITY_DATE, C.COMMUNITY_CNT, C.CATEGORY_NUMBER, C.USER_NUMBER, T.CATEGORY_NAME FROM COMMUNITY C JOIN WW_USER U ON C.USER_NUMBER = U.USER_NUMBER JOIN COMMUNITY_CATEGORY T ON C.CATEGORY_NUMBER = T.CATEGORY_NUMBER
AND C.CATEGORY_NUMBER = #{categoryNumber}
WHERE U.USER_STATUS != '3' AND C.COMMUNITY_STATUS != '0' ORDER BY C.COMMUNITY_NUMBER DESC
)
WHERE ROWNUM <= #{page} * #{amount}
) WHERE RN > ( #{page} - 1) * #{amount}
