mapper.update:
UPDATE COMMUNITY
SET COMMUNITY_TITLE = #{communityTitle} , COMMUNITY_CONTENT = #{communityContent}, COMMUNITY_DATE = SYSDATE
WHERE COMMUNITY_NUMBER = #{communityNumber}

mapper.select:
SELECT (SELECT COUNT(*) FROM COMMUNITY_COMMENT WHERE COMMUNITY_COMMENT.COMMUNITY_NUMBER = C.COMMUNITY_NUMBER) AS COMMENT_CNT ,C.COMMUNITY_NUMBER, C.COMMUNITY_TITLE, C.COMMUNITY_CONTENT, C.COMMUNITY_DATE, C.COMMUNITY_CNT, C.CATEGORY_NUMBER ,C.USER_NUMBER, U.USER_ID ,T.CATEGORY_NAME FROM COMMUNITY C JOIN WW_USER U ON C.USER_NUMBER = U.USER_NUMBER JOIN community_category T ON C.CATEGORY_NUMBER = T.CATEGORY_NUMBER
WHERE C.CATEGORY_NUMBER = #{categoryNumber}
ORDER BY C.COMMUNITY_NUMBER DESC
