mapper.update:
UPDATE COMMUNITY
SET COMMUNITY_TITLE = #{communityTitle} , COMMUNITY_CONTENT = #{communityContent}, COMMUNITY_DATE = SYSDATE
WHERE COMMUNITY_NUMBER = #{communityNumber}

UPDATE COMMUNITY
SET COMMUNITY_CNT = COMMUNITY_CNT+1
WHERE COMMUNITY_NUMBER = #{communityNumber}

mapper.select:
SELECT COUNT(*)
FROM COMMUNITY_COMMENT
WHERE COMMUNITY_NUMBER = #{communityNumber}

SELECT (SELECT COUNT(*) FROM COMMUNITY_COMMENT WHERE COMMUNITY_COMMENT.COMMUNITY_NUMBER = C.COMMUNITY_NUMBER) AS COMMENT_CNT ,C.COMMUNITY_NUMBER, C.COMMUNITY_TITLE, C.COMMUNITY_CONTENT, C.COMMUNITY_DATE, C.COMMUNITY_CNT, C.CATEGORY_NUMBER ,C.USER_NUMBER, U.USER_ID, U.USER_BELONG, U.USER_NICKNAME, U.USER_IDENTITY ,T.CATEGORY_NAME FROM COMMUNITY C JOIN WW_USER U ON C.USER_NUMBER = U.USER_NUMBER JOIN community_category T ON C.CATEGORY_NUMBER = T.CATEGORY_NUMBER
and C.CATEGORY_NUMBER = #{categoryNumber}
where U.USER_STATUS != '3' and C.COMMUNITY_STATUS != '0' ORDER BY C.COMMUNITY_NUMBER DESC
