mapper.update:
UPDATE CS
SET CS_TITLE = #{csTitle}, CS_CONTENT = #{csContent}
WHERE CS_NUMBER = #{csNumber}

mapper.select:
SELECT MC.CAREER_COMPANY, MC.CAREER_TITLE, P.PFP_SYSTEM_NAME, P.PFP_UUID, MC.CAREER_START_DATE, U.USER_NICKNAME FROM WW_USER U LEFT OUTER JOIN USER_PFP P ON U.USER_NUMBER = P.USER_NUMBER LEFT OUTER JOIN MENTOR M ON U.USER_NUMBER = M.USER_NUMBER LEFT OUTER JOIN MENTOR_CAREER MC ON MC.MENTOR_NUMBER = M.MENTOR_NUMBER
WHERE U.USER_NUMBER = #{userNumber}
ORDER BY MC.CAREER_START_DATE DESC

SELECT CS_NUMBER, CS_TITLE, CS_CONTENT, CS_DATE, USER_NUMBER, CS_READ_CNT
FROM(
SELECT ROWNUM RN, CS_NUMBER, CS_TITLE, CS_CONTENT, CS_DATE, USER_NUMBER, CS_READ_CNT
FROM(
SELECT
C.CS_NUMBER, C.CS_TITLE, C.CS_CONTENT, C.CS_DATE, C.USER_NUMBER, CS_READ_CNT
FROM CS C JOIN WW_USER U
ON C.USER_NUMBER = U.USER_NUMBER
ORDER BY C.CS_NUMBER DESC
)

        WHERE ROWNUM <= #{page} * #{amount}

)
WHERE RN > ( #{page} - 1) * #{amount}

mapper.update:
UPDATE CS
SET CS_READ_CNT = CS_READ_CNT+1
WHERE CS_NUMBER = #{csNumber}

mapper.select:
SELECT COUNT(*)
FROM CS_COMMENT
WHERE CS_NUMBER = #{csNumber}
