mapper.select:
SELECT QNA_NUMBER, QNA_TITLE, QNA_CONTENT, QNA_DATE, QNA_CNT, USER_NUMBER, SUB_NAMES, QNA_LIKE_CNT, COMMENT_CNT FROM ( SELECT ROWNUM AS RNUM, QNA_NUMBER, QNA_TITLE, QNA_CONTENT, QNA_DATE, QNA_CNT, USER_NUMBER, SUB_NAMES, QNA_LIKE_CNT, COMMENT_CNT FROM ( SELECT Q.QNA_NUMBER, QNA_TITLE, QNA_CONTENT, QNA_DATE, QNA_CNT, USER_NUMBER, SUB_NAMES, (SELECT COUNT(QG.QNA_NUMBER) FROM QNA_GOOD QG WHERE Q.QNA_NUMBER = QG.QNA_NUMBER) AS QNA_LIKE_CNT, (SELECT COUNT(QC.QNA_NUMBER) FROM QNA_COMMENT QC WHERE Q.QNA_NUMBER = QC.QNA_NUMBER) AS COMMENT_CNT FROM QNA Q JOIN ( SELECT QNA_NUMBER, LISTAGG(SC.SUB_NAME, ',') WITHIN GROUP(ORDER BY QB.SUB_NUMBER) AS SUB_NAMES FROM QS_BRIDGE QB JOIN SUB_CATEGORY SC ON QB.SUB_NUMBER = SC.SUB_NUMBER
WHERE QB.SUB_NUMBER IN
#{cate}
GROUP BY QNA_NUMBER ) S1 ON Q.QNA_NUMBER = S1.QNA_NUMBER WHERE Q.QNA_STATUS != '0' ORDER BY Q.QNA_NUMBER DESC )
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

mapper.update:
update qna
set qna_title = #{qnaTitle}, qna_content = #{qnaContent}, qna_date = sysdate
where qna_number = #{qnaNumber}

UPDATE QNA
SET QNA_CNT = QNA_CNT+1
WHERE QNA_NUMBER = #{qnaNumber}

mapper.select:
SELECT COUNT(*)
FROM QNA_COMMENT
WHERE QNA_NUMBER = #{qnaNumber}
