SELECT BIG_CATEGORY, BOARD_NUMBER, BOARD_TITLE, BOARD_CONTENT, BOARD_DATE, BOARD_CNT, BOARD_COMMENT, BOARD_LIKE, BOARD_GOOD FROM( SELECT ROWNUM RN, BIG_CATEGORY, BOARD_NUMBER, BOARD_TITLE, BOARD_CONTENT, TO_CHAR(BOARD_DATE, 'yyyy-mm-dd') BOARD_DATE, BOARD_CNT, BOARD_COMMENT, BOARD_LIKE, BOARD_GOOD FROM( SELECT 600 BIG_CATEGORY, SHARE_NUMBER BOARD_NUMBER, SHARE_TITLE BOARD_TITLE, SHARE_CONTENT BOARD_CONTENT, SHARE_DATE BOARD_DATE, SHARE_READ_CNT BOARD_CNT, -1 BOARD_COMMENT, -1 BOARD_LIKE, -1 BOARD_GOOD FROM SHARE_FREE LEFT JOIN (FILE_UPLOAD_PATH, FILE_UUID, FILE_SYSTEM_NAME, SHARE_NUMBER FROM share_file) WHERE USER_NUMBER = #{userNumber} ORDER BY BOARD_DATE DESC )
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) WHERE RN > (#{criteria.page} - 1) * #{criteria.amount}

