WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) WHERE RN > (#{criteria.page} - 1) * #{criteria.amount}

SELECT BIG_CATEGORY, BOARD_NUMBER, BOARD_TITLE, COMMENT_CONTENT, COMMENT_DATE, COMMENT_UP, COMMENT_DOWN FROM( SELECT ROWNUM RN, BIG_CATEGORY, BOARD_NUMBER, BOARD_TITLE, COMMENT_CONTENT, TO_CHAR(COMMENT_DATE, 'yyyy-mm-dd') COMMENT_DATE, COMMENT_UP, COMMENT_DOWN FROM( SELECT 200 BIG_CATEGORY, C.CAREERINFO_NUMBER BOARD_NUMBER, CAREERINFO_TITLE BOARD_TITLE, CC.COMMENT_CONTENT COMMENT_CONTENT, CC.COMMENT_DATE COMMENT_DATE, -1 COMMENT_UP, -1 COMMENT_DOWN FROM CAREERINFO C JOIN CAREERINFO_COMMENT CC ON C.CAREERINFO_NUMBER = CC.CAREERINFO_NUMBER WHERE CC.USER_NUMBER = #{userNumber} UNION ALL SELECT 300 BIG_CATEGORY, Q.QNA_NUMBER BOARD_NUMBER, Q.QNA_TITLE BOARD_TITLE, QC.COMMENT_CONTENT COMMENT_CONTENT, QC.COMMENT_DATE COMMENT_DATE, (SELECT COUNT(*) FROM QNA_COMMENT_UD UD WHERE QC.COMMENT_NUMBER = UD.COMMENT_NUMBER AND UD.UD_STATUS = 'u') COMMENT_UP, (SELECT COUNT(*) FROM QNA_COMMENT_UD UD WHERE QC.COMMENT_NUMBER = UD.COMMENT_NUMBER AND UD.UD_STATUS = 'd') COMMENT_DOWN FROM QNA Q JOIN QNA_COMMENT QC ON Q.QNA_NUMBER = QC.QNA_NUMBER WHERE QC.USER_NUMBER = #{userNumber} UNION ALL SELECT 400 BIG_CATEGORY, C.COMMUNITY_NUMBER BOARD_NUMBER, C.COMMUNITY_TITLE BOARD_TITLE, CC.COMMENT_CONTENT COMMENT_CONTENT, CC.COMMENT_DATE COMMENT_DATE, (SELECT COUNT(*) FROM COMMUNITY_COMMENT_UD UD WHERE CC.COMMENT_NUMBER = UD.COMMENT_NUMBER AND UD.UD_STATUS = 'u') COMMENT_UP, (SELECT COUNT(*) FROM COMMUNITY_COMMENT_UD UD WHERE CC.COMMENT_NUMBER = UD.COMMENT_NUMBER AND UD.UD_STATUS = 'd') COMMENT_DOWN FROM COMMUNITY C JOIN COMMUNITY_COMMENT CC ON C.USER_NUMBER = CC.USER_NUMBER WHERE CC.USER_NUMBER = #{userNumber} UNION ALL SELECT 500 BIG_CATEGORY, S.STUDY_NUMBER BOARD_NUMBER, S.STUDY_TITLE BOARD_TITLE, SC.COMMENT_CONTENT COMMENT_CONTENT, SC.COMMENT_DATE COMMENT_DATE, -1 COMMENT_UP, -1 COMMENT_DOWN FROM STUDY S JOIN STUDY_COMMENT SC ON S.STUDY_NUMBER = SC.STUDY_NUMBER WHERE SC.USER_NUMBER = #{userNumber} ORDER BY COMMENT_DATE DESC )
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) WHERE RN > (#{criteria.page} - 1) * #{criteria.amount}

SELECT BIG_CATEGORY, BOARD_NUMBER, BOARD_TITLE, COMMENT_CONTENT, COMMENT_DATE, COMMENT_UP, COMMENT_DOWN FROM( SELECT ROWNUM RN, BIG_CATEGORY, BOARD_NUMBER, BOARD_TITLE, COMMENT_CONTENT, TO_CHAR(COMMENT_DATE, 'yyyy-mm-dd') COMMENT_DATE, COMMENT_UP, COMMENT_DOWN FROM( SELECT 300 BIG_CATEGORY, Q.QNA_NUMBER BOARD_NUMBER, Q.QNA_TITLE BOARD_TITLE, QC.COMMENT_CONTENT COMMENT_CONTENT, QC.COMMENT_DATE COMMENT_DATE, (SELECT COUNT(*) FROM QNA_COMMENT_UD UD WHERE QC.COMMENT_NUMBER = UD.COMMENT_NUMBER AND UD.UD_STATUS = 'u') COMMENT_UP, (SELECT COUNT(*) FROM QNA_COMMENT_UD UD WHERE QC.COMMENT_NUMBER = UD.COMMENT_NUMBER AND UD.UD_STATUS = 'd') COMMENT_DOWN FROM QNA Q JOIN QNA_COMMENT QC ON Q.QNA_NUMBER = QC.QNA_NUMBER WHERE QC.USER_NUMBER = #{userNumber} )
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) WHERE RN > (#{criteria.page} - 1) * #{criteria.amount}

SELECT BIG_CATEGORY, BOARD_NUMBER, BOARD_TITLE, COMMENT_CONTENT, COMMENT_DATE, COMMENT_UPDOWN
FROM(
                SELECT 400 BIG_CATEGORY, C.COMMUNITY_NUMBER BOARD_NUMBER,
                       C.COMMUNITY_TITLE BOARD_TITLE, CC.COMMENT_CONTENT COMMENT_CONTENT,
                       CC.COMMENT_DATE COMMENT_DATE, U.UD_STATUS COMMENT_UPDOWN
                FROM COMMUNITY C
                         JOIN COMMUNITY_COMMENT CC
                              ON C.USER_NUMBER = CC.USER_NUMBER
                         JOIN COMMUNITY_COMMENT_UD U
                              ON CC.COMMENT_NUMBER = U.COMMENT_NUMBER
                WHERE CC.USER_NUMBER = #{userNumber}
                ORDER BY COMMENT_DATE DESC
            )

        WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}

        )
WHERE RN > (#{criteria.page} - 1) * #{criteria.amount}
