mapper.update:
update SHARE_FREE
set SHARE_READ_CNT = SHARE_READ_CNT + 1
where share_number = #{shareNumber}

update SHARE_FREE
set SHARE_TITLE   = #{shareTitle},
    SHARE_CONTENT = #{shareContent}
where SHARE_NUMBER = #{shareNumber}

mapper.select:
SELECT share_number, SHARE_TITLE, SHARE_CONTENT, SHARE_STATUS, SHARE_DATE, USER_NUMBER, SHARE_READ_CNT, USER_ID, file_system_name, file_uuid, file_upload_path, USER_STATUS FROM( select ROWNUM RN, share_number, SHARE_TITLE, SHARE_CONTENT, SHARE_STATUS, SHARE_DATE, USER_NUMBER, SHARE_READ_CNT, USER_ID, file_system_name, file_uuid, file_upload_path, USER_STATUS from( SELECT S.share_number, SHARE_TITLE, SHARE_CONTENT, SHARE_STATUS, SHARE_DATE, S.USER_NUMBER, SHARE_READ_CNT, USER_ID, file_system_name, file_uuid, file_upload_path, U.USER_STATUS FROM SHARE_FREE S JOIN WW_USER U ON S.USER_NUMBER = U.USER_NUMBER LEFT JOIN ( select FILE_UPLOAD_PATH, FILE_UUID, FILE_SYSTEM_NAME, SHARE_NUMBER, RK FROM ( select FILE_UPLOAD_PATH, FILE_UUID, FILE_SYSTEM_NAME, SHARE_NUMBER, RANK() over (PARTITION BY SHARE_NUMBER ORDER BY FILE_NUMBER) RK FROM share_file ) WHERE RK = 1 ) J ON S.SHARE_NUMBER = J.share_number ORDER BY S.share_number DESC )
where ROWNUM <= #{page} * #{amount}
) WHERE RN > ( #{page} - 1) * #{amount}
